mapper.select:
SELECT user_seq
  FROM users
 WHERE user_email = #{userEmail}

SELECT file_uuid
  FROM user_file
 WHERE user_seq = #{userSeq}

mapper.update:
UPDATE users SET user_name = #{userName}, user_password = #{userPassword}, 
	user_message = #{userMessage}
WHERE user_email = #{userEmail}

mapper.select:
SELECT board_id
  FROM board
 WHERE user_seq = #{userSeq}

SELECT user_seq, user_name, user_message
  FROM users
 WHERE user_seq = #{userSeq}

mapper.update:
    update friends SET status = #{mode}
	 where (user_seq = #{userSeq} and friend_seq = #{friendSeq})
        or (user_seq = #{friendSeq} and friend_seq = #{userSeq})
